	@Override
	protected void doGet(HttpServletRequest request, HttpServletResponse response) 
			 throws ServletException, IOException { 
		 // reading the user input 
		 String color= request.getParameter("color"); 
		 String name= request.getParameter("name");
		 String email= request.getParameter("email");
		 
		 boolean matches = Pattern.compile(ADDRESS_PATTERN).matcher(email).matches();
		 String output;
		 if(!matches) {
			 output = "Here is your color but the email you provided is not correct :)";
		 } else {
			 output = "We got your name : " + name + " <br>" 
					 + "We event got your email : " + email;
		 }
		 PrintWriter out = response.getWriter(); 
		 out.println ( "<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" "
		 		+ "\"http://www.w3.org/TR/html4/loose.dtd\">\n" + "<html> \n" 
				 + "<head> \n" 
		 		+ "<meta http-equiv=\"Content-Type\" content=\"text/html; charset=ISO-8859-1\"> \n" 
				 + "<title> First JSP Page </title> \n" 
		 		+ "</head> \n" 
				 + "<body> \n" 
		 		+ "<font size=\"12px\" color=\"" + color + "\">" 
				 + output
		 		+ "</font> \n" 
		 		+ "</body> \n" 
		 		+ "</html>" ); 
	 }
